INSERT INTO `questions` VALUES (1,'<p>معنى توحيد الربوبية : إفراد الله عز وجل بـ:</p>','[\"الخلق\", \"الملك\", \"التدبير\"]','جميع ما ذكر',1,NULL,'2022-01-13 11:08:49','2022-01-13 11:11:30'),(2,'<p>الله رب العالمين<br />فما معنى العالمين؟</p>','[\"أهل العلم\", \"الذين يدعون العلم\"]','كل من سوى الله فهو من العالمين',1,NULL,'2022-04-07 13:24:09','2022-04-07 20:24:52'),(3,'<p>أي من التالي من خصال الفطرة</p>','[\"إعفاء اللحية\", \"تقليم الأظافر\", \"قص الشارب\"]','جميع ما ذكر',3,NULL,'2022-04-07 13:32:53','2022-04-07 13:32:53'),(4,'<p>إذا كان الإنسان يعلم أنه لم يخلق نفسه-لأنه لم يكن موجوداً قبل ذلك-،<br />وأنه ليس بخالق -لأنه ناقص-</p>\r\n<p>وأنه لم يُخلق من دون شيء -وخصوصا وهو بهذه الدقة من الخلق-&nbsp;</p>\r\n<p>فهذا يدل على وجود رب خالق له..</p>','[\"استدلال خاطئ\"]','استدلال صحيح',1,NULL,'2022-04-07 13:38:57','2022-04-07 13:38:57'),(5,'<p>معنى لا إله إلا الله</p>','[\"لا خالق إلا الله\", \"لا معبود موجود إلا الله\"]','لا معبود بحق إلا الله',4,NULL,'2022-04-07 13:42:09','2022-04-07 13:42:09'),(6,'<p>إذا كنت في البرية مثلا: فما الشيء الذي لا يجوز الاستنجاء به مما يلي؟</p>','[\"المنديل\", \"الورق الخشن\", \"الحجر الصغير\"]','العظم',36,NULL,'2022-04-07 13:51:45','2022-04-07 13:51:45'),(7,'<p>الإنسان يولد على توحيد الله تعالى بشكل عام.. لكنه قد يتأثر فيقع في الشرك ونحوه</p>\r\n<p>&nbsp;</p>','[\"خطأ\"]','صحيح',5,NULL,'2022-04-07 13:56:27','2022-04-07 13:56:27'),(8,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">&nbsp;كيف تكون العبادة لله؟</span></p>','[\"بالعبادات الباطنة؛ كالمحبة والخوف والرجاء، لأنها هي الإيمان الحقيقي.\", \"بالعبادات الظاهرة؛ كالدعاء والسجود والذبح، لأنا مأمورون بالظاهر\"]','العبادة اسم جامع للعبادات الظاهرة والباطنة',6,NULL,'2022-04-07 13:58:23','2022-04-07 13:58:23'),(9,'<p>من أدلة توحيد الله تعالى بالمحبة</p>','[\"{زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوَاتِ مِنَ النِّسَاءِ وَالْبَنِينَ }\", \"{إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ  }\"]','قال تعالى: ﴿وَمِنَ النَّاسِ مَن يَتَّخِذُ مِن دُونِ اللّهِ أَندَادًا يُحِبُّونَهُمْ كَحُبِّ اللّهِ وَالَّذِينَ آمَنُواْ أَشَدُّ حُبًّا لِّلّه ﴾ .',6,NULL,'2022-04-07 14:02:37','2022-04-07 14:02:37'),(10,'<p><span style=\"color: #181c32; font-family: Lotus Linotype, serif;\"><span style=\"font-size: 22.75px;\">الشرك&nbsp; أن يجعل لله تعالى نداً؛ في شيء من العبادة أو صفات الربوبية.</span></span></p>','[\"خاطئ\"]','صحيح',7,NULL,'2022-04-07 14:04:38','2022-04-07 14:04:38'),(11,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">من أمثلة الشرك الأكبر .</span></p>','[\"أن يتخذ وسائط من بشر أو حجر  يدعوهم بالغيب، أو يذبح لهم.\", \"أن يرى للنجم ونحوه تصرفًا مُستَقلًا في الكون\"]','جميع ما ذكر',7,NULL,'2022-04-07 14:07:15','2022-09-01 11:34:51'),(12,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">ما حكم تعليق تميمة تحمي عن العين؛ كالخرزة الزرقاء؟</span></p>','[\"شرك أكبر يخرج من الملة\", \"مكروه\", \"جائز عند الحاجة\"]','شرك أصغر، يحرم وإن كان لا يخرج من الملة',7,NULL,'2022-04-07 14:10:00','2022-04-07 14:12:03'),(13,'<p>ما معنى الصيام؟</p>','[\"الإمساك عن الأكل والشُّرب في شهر رمضان\", \"كل حمية تفيد الجسم وتصفي الذهن\"]','التعبُّدُ لله سبحانه وتعالى، بالإمساك عن الأكل والشُّرب والجماع، وسائر المفطِرات في وقت مخصوص.',62,NULL,'2022-04-07 14:17:21','2022-04-07 14:17:21'),(14,'<p>ما وقت الصوم المشروع له؟</p>','[\"الإمساك عن المفطرات من طلوع الشمس إلى غروبها\", \"من حين أن ينوي الصيام حتى أذان المغرب\"]','من طلوع الفجر حتى غروب الشمس',62,NULL,'2022-04-07 14:26:48','2022-04-07 14:31:45'),(15,'<p>بماذا نسمي الله تعالى وبماذا نصفه؟</p>','[\"نفكر في أجمل الأسماء والصفات ونسميه بها\", \"لا نسميه بشيء ولا نصفه بشيء لأنه لا أحد يعلم اسمه أو صفته إلا الرسول صلى الله عليه وسلم\"]','نسميه ونصفه بما سمى به نفسه أو وصف به نفسه، أو جاء عن الرسول صلى الله عليه وسلم',8,NULL,'2022-04-07 14:36:16','2022-04-07 14:36:16'),(16,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">نبيي محمد صلى الله عليه وسلم، أرسله الله - تعالى :</span></p>','[\"للعرب فقط\", \"لقريش ومن حولها\"]','للناس كافة',9,NULL,'2022-04-07 14:38:57','2022-04-07 14:38:57'),(17,'<p>كيف يؤدي الإنسان الشهادتين للدخول في الإسلام؟</p>','[\"يكفي أن يعتقد بقلبه أنه لا إله إلا الله.\", \"لا بد أن يعلن الشهادة أمام جمع من الناس\"]','يقول بلسانه مؤمناً بذلك:  أشهَدُ أنْ لا إلهَ إلَّا اللهُ وأنَّ مُحمَّدًا رسولُ اللهِ.',10,NULL,'2022-04-07 14:44:16','2022-04-07 14:44:16'),(18,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">&nbsp;أن تؤمِن بالله، وملائكته، وكتبه، ورسله واليوم الآخِر، والقَدَر خيره وشره.</span></p>\r\n<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">هذه تسمى:</span></p>','[\"صفة الشهادة\", \"توحيد الربوبية\"]','أركان الإيمان',11,NULL,'2022-04-07 14:47:45','2022-04-07 14:47:45'),(19,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">الإيمان بالغيب أن نؤمن بكل ما أخبَرَنا اللهُ تعالى به، </span><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">أو أخبَرَنا به رسولُه -صلى الله عليه وسلم-،</span><br style=\"box-sizing: border-box; font-family: \'Lotus Linotype\', serif; color: #181c32; font-size: 22.75px; background-color: #ffffff;\" /><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">مما يغيب عنا من الأُمورِ الماضية، أو المستقبلية</span></p>','[\"صحيح لكن دون ذكر الأمور المستقبلية\", \"خطأ\", \"صحيح ولكن خاص بما أخبر الله تعالى به في القرآن\"]','صحيح',28,NULL,'2022-04-07 14:58:34','2022-04-07 14:58:34'),(20,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">الزكاة الواجبة هي كل ما يعطيه المسلم للفقير</span></p>','[\"صحيح\", \"خاطئ، لأن الزكاة يعطيها المسلم والكافر\"]','خاطئ، لأن الزكاة جزء يسير يجب على المسلم في ماله إذا بلغ مقدارا معيناً',57,NULL,'2022-04-07 15:03:17','2022-04-07 15:03:17'),(21,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">&nbsp;﴿ يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ﴾</span></p>\r\n<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">هذه الآية تدل على فضل الصيام، كيف ذلك؟</span></p>','[\"لأن الله كتب الصيام علينا أي أوجبه علينا\", \"لأن الله كتب الصيام وأوجبه على الأمم السابقة كاليهود والنصارى\", \"لأن الله جعل الصيام سبباً للتقوى\"]','جميع ما ذُكر هنا',62,NULL,'2022-04-07 15:05:50','2022-04-09 10:27:23'),(22,'<p>على من يجب صيام رمضان؟</p>','[\"الصغير القادر على الصوم\", \"الذكر والأنثى من المسلمين\", \"جميع ما ذكر\"]','المسلم العاقل البالغ القادر على الصوم',63,NULL,'2022-04-07 15:08:36','2022-04-07 15:08:36'),(23,'<p>مريض أخبره الأطباء الثقات أن صومه في هذا الوقت سيزيد مرضه</p>\r\n<p>فهل يجب عليه الصوم؟</p>','[\"يجب عليه حتى يحس بالتعب وحينئذ يجوز له الفطر\", \"يجب عليه الصوم، وعليه أن يصبر وسيؤجر على مرضه\"]','لا يجب عليه الصوم، ومتى قدر صام',64,NULL,'2022-04-07 15:11:24','2022-04-09 10:50:37'),(24,'<p>ما الشيء الذي لا يفطر مما يلي:</p>\r\n<p>&nbsp;</p>','[\"الأكل أو الشرب اليسير\", \"أخذ الإبر المغذية\"]','النوم أكثر اليوم أو الاغتسال',65,NULL,'2022-04-07 15:20:58','2022-04-07 15:20:58'),(25,'<p>معنى&nbsp; <span style=\"background-color: #ffffff; color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px;\">الحج هو</span></p>','[\"الذهاب إلى مكة\", \"أن يحج القلب أولا ويقصد الله تعالى بالعبادة\"]','التعبُّدُ لله -سبحانه وتعالى-،   بقصدِ بيت الله الحرام،    وبعض الأماكن حوله ؛    لأداءِ عبادات،    بصفةٍ خاصة، في أوقات خاصة.',66,NULL,'2022-04-07 15:23:14','2022-04-07 15:23:14'),(26,'<p><span style=\"color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px; background-color: #ffffff;\">كسبُ المال سبب للشر ولهذا نهى الإسلام عنه</span></p>','[\"صحيح ولكن يجوز الاكتفاء من المال بقدر الضرورة\", \"صحيح\"]','خطأ بل هو مباح بشروط',70,NULL,'2022-04-07 15:25:35','2022-04-07 15:25:35'),(27,'<p>الواجب ما يلزم الإنسان أن يفعله ويؤجر عليه، ويأثم بتركه<br />مثل الصبر عن الجزع عند المصائب، وغض البصر عن رؤية المحرمات.</p>','[\"صحيح ولكن الصبر ليس بواجب\", \"صحيح ولكن الواجب لا يؤجر عليه\", \"خطأ\"]','صحيح',85,NULL,'2022-04-07 15:53:07','2022-04-07 15:53:07'),(28,'<p><span style=\"background-color: #ffffff; color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px;\">من نشأ على دين النصرانية أو اليهودية، وكان حسن الأخلاق مع الآخرين</span></p>\r\n<p><span style=\"background-color: #ffffff; color: #181c32; font-family: \'Lotus Linotype\', serif; font-size: 22.75px;\">فهل يدعى إلى الإسلام وهل يجب عليه الإسلام؟</span></p>\r\n<p>&nbsp;</p>','[\"لا يدعى، لأنه يعتقد أنه على دين صحيح\", \"أهم شيء حسن الخلق، وله دينه ولنا ديننا\"]','يدعى، ومن يبتغي غير الإسلام فلن يقبل منه، ويبقى على حسن خلقه',92,NULL,'2022-04-07 19:00:39','2022-04-07 19:00:39'),(29,'<p>إذا سافر المسلم في رمضان إلى بلد بعيد عن بلده (كمن سافر لمكة 100 كم)&nbsp;</p>','[\"يجب عليه الفطر لأنه مسافر\", \"لا يجوز له الفطر لأنه في رمضان\"]','يجوز له الصيام ويجوز له الفطر',64,NULL,'2022-04-09 10:55:30','2022-04-09 10:55:30'),(30,'<p>إذا أفطر المسلم في رمضان لوجود عذر، ثم انتهى رمضان&nbsp;</p>\r\n<p>فمتى يجب عليه القضاء؟</p>','[\"يجب عليه القضاء في شوال لأنه واجب عليه\", \"يجوز له أن يؤخره متى شاء ولو بعد سنة\"]','يجب عليه القضاء قبل دخول رمضان القادم لكن إذا بكّر فهو أفضل لأنه لا يدري متى الموت',64,NULL,'2022-04-09 10:59:18','2022-04-09 10:59:18'),(31,'<p>إذا نسي في نهار رمضان فأكل أو شرب</p>\r\n<p>فماذا يفعل؟</p>','[\"يتم صومه ويقضي يوماً مكانه\", \"لا يتم صومه لأنه قد بطل، ويقضي يوماً مكانه\"]','يتم صومه ولا شيء عليه',65,NULL,'2022-04-09 11:01:22','2022-04-09 11:01:22'),(32,'<p>لو أراد الصائم المتزوج أن يقبل زوجته في نهار رمضان، فهل له ذلك؟</p>','[\"لا يجوز له التقبيل إلا في الليل\", \"يجوز في صيام غير رمضان ولا يجوز في صيام رمضان\"]','يجوز إلا إذا خشي أن يجره ذلك إلى الجماع أو إنزال المني',65,NULL,'2022-04-09 11:05:37','2022-04-09 11:05:37'),(33,'<p>بنت صغيرة ، حاضت في شهر رمضان،</p>\r\n<p>فاستحت وأكملت صيامها أيام حيضها ، فماذا عليها؟</p>','[\"أخطأت، وعليها أن تتوب، وليس عليها قضاء\", \"أصابت، وليس عليها قضاء لأنها صامت فعلا\", \"أخطأت وعليها القضاء والكفارة\"]','أخطأت، ولا حياء من الحق، وعليها أن تصوم أياماً بعدد أيام حيضها التي صامتها',63,NULL,'2022-04-10 01:27:07','2022-04-10 01:27:07'),(34,'<p>امرأة كبيرة في السن، بحيث إنها أصبحت لا تدرك معنى الصوم<br />فماذا عليها؟</p>','[\"لا يجب عليها الصوم، ولكن أولادها يطعمون عنها في كل يوم مسكينا\", \"يجب على أولادها إعانتها على الصوم، بحيث يمنعون عنها الطعام نهاراً إن كان لا يضرها\", \"يصوم أولادها عنها بعد رمضان\"]','لا يجب عليها الصوم، ولا يجب الإطعام عنها لأن الصوم لم يجب عليها',63,NULL,'2022-04-10 01:56:48','2022-04-10 01:56:48');
